mapper.select:
select a.article_id, a.article_type, a.article_name, a.article_nameEn, a.create_time, a.create_by, a.update_by, a.update_time, a.del_flag
, b.file_id, b.file_path ,b.real_name
from sys_home_article a  left join sys_file  b on a.file_id= b.file_id


     and article_type = #{articleType}
     and article_name like concat('%', #{articleName}, '%')
     and article_nameEn = #{articleNameen}
     and file_id = #{fileId}

select a.article_id, a.article_type, a.article_name, a.article_nameEn, a.create_time, a.create_by, a.update_by, a.update_time, a.del_flag
       , b.file_id, b.file_path ,b.real_name
from sys_home_article a  left join sys_file  b on a.file_id= b.file_id

     and article_type = #{articleType}
     and article_name like concat('%', #{articleName}, '%')
     and article_nameEn = #{articleNameen}
     and file_id = #{fileId}

mapper.update:
update sys_home_article
article_type = #{articleType}, article_name = #{articleName}, article_nameEn = #{articleNameen}, file_id = #{fileId}, create_time = #{createTime}, create_by = #{createBy}, update_by = #{updateBy}, update_time = #{updateTime}, del_flag = #{delFlag},
where article_id = #{articleId}
